/** @format */

import React, { FC } from "react";
import styled from "styled-components";
import Icon from "../icons/Icon";
import {
  LayerGreyButtonsHover,
  LayerWhiteFirstActive,
  LayerWhiteFirstDefault,
  LayerWhiteFirstHover,
  LayerYellowDefault,
  LayerYellowHover,
} from "../layerStyles/LayerStyles";
import Typography from "../typography/Typography";
import { TagProps } from "./Tag.types";

const StyledButton = styled.button<TagProps>`
  pointer-events: all;
  user-select: none;
  position: relative;
  cursor: pointer;
  box-sizing: border-box;
  width: auto; /* 150px */
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;

  //ADD THEME-RADII
  border-radius: 10px;

  //ADD THEME-SPACE?
  height: 36px;

  //FOR SMALLICONBUTTON  â€“ is this really  correct?
  width: ${(props) => (props.width === "max" ? "100%" : "auto")};

  pointer-events: all;

  //ADD THEME-SPACE?
  padding: 14px 10px 14px 10px;

  transform: ${({ hide }) => (hide ? "scale(0.8)" : "scale(1)")};
  opacity: ${({ hide }) => (hide ? "0" : "1")};
  /* display: ${({ hide }) => (hide ? "none" : "flex")}; */
  display: flex;
  transition: 0.4s;

  animation: tagsAnimation 0.4s;
  @keyframes tagsAnimation {
    0% {
      transform: scale(0.8);
      opacity: 0;
    }
    100% {
      transform: scale(1);
      opacity: 1;
    }
  }

  ${(props) => (props.active ? LayerWhiteFirstActive : LayerWhiteFirstDefault)}

  &:hover:enabled {
    ${(props) => (props.active ? LayerWhiteFirstActive : LayerWhiteFirstHover)}
  }

  &:active:enabled {
    ${(props) => LayerWhiteFirstActive}
  }
`;

const IconWrapper = styled.div<TagProps>`
  padding-right: ${(props) => (props.text === undefined ? "0px" : "5px")};
`;

const Tag: FC<TagProps> = ({
  text,
  icon,
  iconColor,
  variant,
  width,
  hide,
  active,
  onClick,
  ...rest
}) => (
  <StyledButton
    type="button"
    text={text}
    variant={variant}
    leadingIcon={icon}
    width={width}
    active={active}
    onClick={onClick}
    hide={hide}
    {...rest}
  >
    {icon === "all-topics" ? (
      <IconWrapper text={text}>
        <svg
          width="20"
          height="16"
          viewBox="0 0 16 16"
          version="1.1"
          style={{ marginTop: "3px" }}
        >
          <image
            x="0"
            y="0"
            width="16"
            height="16"
            xlinkHref="data:image/png;base64,"
            stroke="none"
            strokeWidth="1"
            fill="none"
            fillRule="evenodd"
          />
        </svg>
      </IconWrapper>
    ) : (
      icon && (
        <IconWrapper text={text}>
          <Icon
            icon={icon}
            transform="scale(0.75)"
            color={iconColor || "black"}
          />
        </IconWrapper>
      )
    )}

    <Typography variant="bodySm"> {text}</Typography>
  </StyledButton>
);

export default Tag;
